signal = ''

class Buffer():

    def __init__(self, buffer) -> None:
        
        self.buffer = buffer
    
    def duplicate_characters(self, start_position, characters) -> bool:
        test_list = self.buffer[start_position:start_position + characters]
        test_set = set()
        for item in test_list:
            test_set.add(item)
        return len(test_list) != len(test_set)

    def get_buffer_length(self) -> int:
        return len(self.buffer)

    def __str__(self) -> str:
        return self.buffer

buffer = Buffer(signal)

for i in range(buffer.get_buffer_length()):
    if buffer.duplicate_characters(i,4):
        print(i, buffer.duplicate_characters(i,4))
    else:
        break

for message in range(buffer.get_buffer_length()):
    if buffer.duplicate_characters(message,14):
        print(message, buffer.duplicate_characters(message,14))
    else:
        break

print(f'Start of signal {i+4.}, message at {message+14}')

